package Array;import java.util.ArrayList;import java.util.List;public class lc204 {    class Solution {        public int countPrimes(int n) {            List<Integer> primes = new ArrayList<>();            int currentPrime = 0;            while (currentPrime < n) {                for (int prime: primes) {                    if (currentPrime % prime == 0) {                        currentPrime++;                        continue;                    }                }                primes.add(n);                currentPrime++;             }             return primes.size();        }    }}