package Array;class Solution {    public int minChanges(int n, int k) {        String binN = Integer.toBinaryString(n);        String binK = Integer.toBinaryString(k);        if (binK.length() < binN.length()) {            binK = "0".repeat(binN.length() - binK.length()) + binK;        } else {            binN = "0".repeat(binK.length() - binN.length()) + binN;        }        int result = 0;        for (int i = 0; i < binN.length(); i++) {            if (binK.charAt(i) == '1' && binN.charAt(i) == '0') return -1;            if (binK.charAt(i) == '0' && binN.charAt(i) == '1') result++;        }        return result;    }}