package BinarySearch;import java.util.Arrays;public class lc1608 {    class Solution {        public int specialArray(int[] nums) {            Arrays.sort(nums);            if (nums.length <= nums[0]) {                return nums.length;            }            for (int i = 1; i < nums.length; i++) {                if ((nums.length - i) > nums[i - 1] && (nums.length - i) <= nums[i]) {                    return (nums.length - i);                }            }            return -1;        }    }}