//633. Sum of Square Numbers//        Solved//        Medium//        Topics//        Companies//        Given a non-negative integer c, decide whether there're two integers a and b such that a2 + b2 = c.////////        Example 1:////        Input: c = 5//        Output: true//        Explanation: 1 * 1 + 2 * 2 = 5//        Example 2:////        Input: c = 3//        Output: false//////        Constraints:////        0 <= c <= 231 - 1package HashTable;import java.util.ArrayList;import java.util.HashMap;import java.util.List;public class lc633 {    class Solution {        public boolean judgeSquareSum(int c) {            HashMap<Integer, Integer> map = new HashMap<>();            for (int i = 0; i <= Math.sqrt(c); i++) {                Integer currentSquare = i * i;                map.putIfAbsent(currentSquare, 0);                if (map.containsKey(c - currentSquare)) {                    return true;                }            }            return false;        }    }}