package Heaps;import java.util.PriorityQueue;import java.util.Queue;public class lc215 {    class Solution {        public int findKthLargest(int[] nums, int k) {            Queue<Integer> heap = new PriorityQueue<>((x, y) -> -Integer.compare(x, y));            for (int num: nums)                heap.add(num);            for (int i = 0; i < k - 1; i++) {                heap.remove();            }            return heap.remove();        }    }}