package LinkedLists;public class lc82 {    class Solution {        public ListNode deleteDuplicates(ListNode head) {            ListNode temp = head;            if (head == null || head.next == null) return head;            while (temp != null && temp.next != null) {                if (temp.next.val == temp.val) {                    temp.next = temp.next.next;                } else if (temp.next.next == null) {                    temp.next = null;                    return head;                } else if (temp.next.val != temp.val && temp.next.val != temp.next.next.val) {                    temp = temp.next;                } else {                    temp.next = temp.next.next;                }            }            return head;        }    }}