package Stacks;import java.util.Stack;public class lc1963 {    class Solution {        public int minSwaps(String s) {            Stack<Character> stack = new Stack<>();            stack.push(s.charAt(0));            for (int i = 1; i < s.length(); i++) {                if (stack.size() == 0)                    stack.push(s.charAt(i));                if (stack.peek() == '[' && s.charAt(i) == ']') {                    stack.pop();                } else {                    stack.push(s.charAt(i));                }            }            return stack.size() / 2;        }    }}