// generate postorderTraversalpackage Trees;import java.util.ArrayList;import java.util.List;public class lc145 {    class Solution {        List<Integer> result = new ArrayList<>();        public List<Integer> postorderTraversal(TreeNode root) {            if (root != null) {                postorderTraversal(root.left);                postorderTraversal(root.right);                result.add(root.val);            }            return result;        }    }}